INSERT INTO `tp_ad` VALUES ('21', '1587628305', '1587628659', '21', '1', '8', '兰州1934西北防疫处原址', '/uploads/20200423/bda0c13bdb46860ac6df1b20adbeae52.jpg', '', '1934', '西北防疫处（兰州生物制品研究所前身）在兰州成立，以调查及防治西北各省兽疫与制造防治急需之兽用制品为主要任务，并辅以协助民众防疫之任务。');
INSERT INTO `tp_ad` VALUES ('22', '1587628863', '1587716554', '22', '1', '8', '1949新中国成立前后', '/uploads/20200423/5f81f1c7dd7246d52627f4ebf3132bad.jpg', '', '1949', '新中国成立前后，中央防疫实验处先后更名为中央防疫处、天坛防疫处。12月，移交中华人民共和国卫生部直接领导。同年，华东人民制药公司上海生物制品厂（上海生物制品研究所前身）成立，图为上海生物制品厂建筑。');
INSERT INTO `tp_ad` VALUES ('23', '1587715163', '1587716626', '23', '1', '10', '发展理念', '/uploads/20200424/1565b5076009519e24c6a459495f6bd5.jpg', '', '', '创新\n协调\n绿色\n开放\n共享');
