INSERT INTO `tp_ad` VALUES ('26', '1587715440', '1587716569', '26', '1', '10', '大力拓展开放式创新', '/uploads/20200424/ccacfc04878551f4ff33fdc73cbb48f7.jpg', '', '', '中国生物坚定不移地走国内外科技合作交流的开放式创新道路，通过引进国内外新型产品及先进技术，将技术合作交流与人才队伍培养紧密结合，在新型疫苗、抗体药物、新型佐剂、凝血因子等新技术和新产品引进方面与国际企业及机构进行了深入的合作探讨和卓有成效的项目交流。');
