INSERT INTO `tp_cate` VALUES ('39', '1587696802', '1587709135', '1', '1', '人工疫苗', 'HUMAN VACCINE', '', '27', '22', '', '/uploads/20200424/f8ff761d20042cc78db9cee29a493110.jpg', '/uploads/20200424/2c5100d8d08ac1023ebf50d02a5f3a13.png', '', '', '', '公司人用疫苗可年产50种，年产量超7亿剂次，居全球第六位，一类疫苗产品覆盖了国家免疫规划所针对的全部15种疾病，市场占有率达80%。乙脑疫苗、bOPV疫苗先后通过WHO预认证，是国内首家获WHO预认证的疫苗企业。', '', '', '0', '0', '0', '0');
INSERT INTO `tp_cate` VALUES ('40', '1587696979', '1587712011', '2', '1', '血液制品', 'BLOOD PRODUCT', '', '27', '22', '', '/uploads/20200424/579069ad466922f5084af077ecfed566.jpg', '/uploads/20200424/5a8a96c4fe99aeb3eda11eff24d08a58.png', '', '', '', '公司目前拥有5家血液制品生产企业和57家单采血浆站，在产血液制品11种，血浆采浆量国内第一，2018年采浆量达1568吨。', '', '', '0', '0', '0', '0');
INSERT INTO `tp_cate` VALUES ('41', '1587697133', '1587712021', '3', '1', '医学美容', 'MEDICAL AESTHETICS', '', '27', '22', '', '/uploads/20200424/d482f3b04148d35afa1a0567cfc60d93.jpg', '/uploads/20200424/930c438fd4e0da5d38ced0354a48f478.png', '', '', '', '作为国内唯一经政府认证批准的肉毒毒素生产商，医学美容产品得到市场高度认可，产品市场占有率达77%，排名第一。', '', '', '0', '0', '0', '0');
INSERT INTO `tp_cate` VALUES ('42', '1587697260', '1587712031', '4', '1', '动物保健', 'ANIMAL HEALTH', '', '27', '22', '', '/uploads/20200424/27bc4abb5c7a30a67a84f2c17e9ed751.jpg', '/uploads/20200424/ddf67d89c331c89f8b0ae504dd1f539d.png', '', '', '', '2015年8月开始进入动物保健行业，经过重组整合国药动保板块正式启航，初步完成动物保健产业布局，向社会提供动物疫苗及相关兽用生物制品的全方位、高质量服务。目前，两家生产企业，年产禽用疫苗41.8亿羽份、畜用疫苗2.98亿头份，市场规模已进入行业前十名。', '', '', '0', '0', '0', '0');
INSERT INTO `tp_cate` VALUES ('43', '1587697364', '1587712042', '5', '1', '抗体及治疗药物', 'ANTIBODY AND THERAPEUTICS', '', '27', '22', '', '/uploads/20200424/b243423379131910280acce13a47f725.jpg', '/uploads/20200424/a2cf75405350fb184f315b1f44e027e3.png', '', '', '', '公司是中国最早研发抗体药物的企业之一，具备完整的抗体药物产业链条和特色鲜明的分子诊断大产业链，武汉生物制品研究所的注射用鼠源性人T淋巴细胞CD3抗原单克隆抗体是中国最早批准上市的国内自行研制的抗体产品。公司把抗体药物定位为未来主攻方向之一，正在集中内部的抗体药物资产和研发资源，加强与国内外研发机构的技术合作和技术引进，加快研发进度，力争尽快取得成果。', '', '', '0', '0', '0', '0');
INSERT INTO `tp_cate` VALUES ('44', '1587697456', '1587712050', '6', '1', '医学诊断', 'MEDICAL DIAGNOSTICS', '', '27', '22', '', '/uploads/20200424/169db853933726ddf55439d028179090.jpg', '/uploads/20200424/4af5fd02cb851b3bca75105dcd62e558.jpg', '', '', '', '公司大力发展诊断业务板块，走贸、工、科的发展路线，努力成为国内细分领域龙头企业。宫颈癌筛查精准预测诊断国际领先。', '', '', '0', '0', '0', '0');
